for (let i = 0; i < chargeArray.length; i++) {
  const dx = testChargePosition.x - chargeArray[i].position.x;
  const dy = testChargePosition.y - chargeArray[i].position.y;
  const distance = Math.sqrt(dx * dx + dy * dy);
  const field = chargeArray[i].charge / (distance * distance);
  
  distances.push(distance);
  fields.push(field);
}

// Calculate the total electric field by summing up the individual fields
let totalField = { x: 0, y: 0 };
for (let i = 0; i < chargeArray.length; i++) {
  const dx = testChargePosition.x - chargeArray[i].position.x;
  const dy = testChargePosition.y - chargeArray[i].position.y;
  totalField.x += fields[i] * (dx / distances[i]);
  totalField.y += fields[i] * (dy / distances[i]);
}

