// The new position is calculated by adding the product of the net force in the x-direction and the time step (`netForceX * dt`)
// to the current x position of the test charge, and similarly for the y-direction (`netForceY * dt`)

if (hitObstacle === true) {
    // If the 'hitObstacle' variable is true, set the test charge position to a specific point (0.075, 0.5)
    setTestChargePosition(new phet.dot.Vector2(0.1, 0.5));
} else if (hitGoal === true) {
    // If the 'hitGoal' variable is true, wait for 3 seconds and then set the test charge position to a specific point (0.075, 0.5) using a timer
    // setTimeout(function () {
    setTestChargePosition(new phet.dot.Vector2(0.1, 0.5));
    // }, 3000);
} else if (leftView === true) {
    // If the 'leftView' variable is true, log the current test charge position (x, y) and set the position to a specific point (0.075, 0.5)
    phet.paperLand.console.log(testChargePosition.x, testChargePosition.y);
    setTestChargePosition(new phet.dot.Vector2(0.1, 0.5));
} else {
    // Otherwise, calculate the new test charge position by adding the product of the net force in the x-direction and the time step to the current x position,
    // and similarly for the y-direction, and set the test charge position to the calculated position
    setTestChargePosition(new phet.dot.Vector2(testChargePosition.x + (netForceX / 100 * dt), testChargePosition.y + (netForceY / 100 * dt)));
}

