if ( BoundsRotationModel >= 1 && BoundsRotationModel <= 4 ) {

    // find all of the entries of tile data that are on the left side
    const rightTiles = tileData.filter( data => !data.onLeftSide );
    return scratchpad.getTermDescriptionForSide( rightTiles, 'right' );

} else if ( BoundsRotationModel >= 6 && BoundsRotationModel <= 9 ) {

