}

customCodeContainer.onProgramAddedCode:
// A function to generate a description for a particular side.
scratchpad.getTermDescriptionForSide = ( tileData, leftOrRightString ) => {
    const xSquaredTiles = tileData.filter( data => data.term === 2 );   //xSquared
    const xTiles = tileData.filter( data => data.term === 1 );         // x
    const onesTiles = tileData.filter( data => data.term === 0 );      // one

    let description= '';

    const handleTile = ( tile, term ) => {
        if ( tile.negative && tile.count !== 0 ) {

            // always describe if it is a negative operation
            description += 'minus';
        }
        else if ( description.length > 0 && tile.count !== 0 ) {

            // if there are terms before this one, include the operation
            description += 'plus';
        }
        if ( tile.count > 0 ) {

            // if we include the 'one' description OR there are more than one
            // in the count, include the number
            description += ` ${tile.count}`; // ${tile.count + 1}`
        }
        description += `${term}`; 
    }

    xSquaredTiles.forEach( tile => handleTile( tile, 'x squared' ) );
    xTiles.forEach( tile => handleTile( tile, 'x' ) );
    onesTiles.forEach( tile => handleTile( tile, '' ) );

    if ( description ) {
        return description + leftOrRightString + ' of equals.';
    }
    else {
        return 'No tiles ' + leftOrRightString + ' of equals.';
    }
};

