linkListeners.controlFunctionString:
// Use internal Paper Playground function to get a reference to the tileData array, without
// add a listener to it so that the balance isn't checked EVERY time the array changes.
const tileDataProperty = phet.paperLand.getModelComponent( 'tileData' );
if ( tileDataProperty ) {
    const tileData = tileDataProperty.value;

    // find all of the entries of tile data that are on the left side
    const rightTiles = tileData.filter( data => !data.onLeftSide );
    const leftTiles = tileData.filter( data => data.onLeftSide );

    // This is a function that we use to count the "number" of terms for a given term. We go 
    // through the tiles and increment by the number of terms on that tile if positive, and 
    // decrement by the number of terms if negative.
    const createAccumulatorFunction = term => {
        return ( accumulator, data ) => {
            if ( data.term === term ) {
                const numberOfTerms = data.count;
                return data.negative ? accumulator - numberOfTerms : accumulator + numberOfTerms;
            }
            else {
                return accumulator;
            }
        }
    }

    // Remember, terms are mapped to a number. 'xSquared' is 2, 'x' is 1 and 'ones' is 0.
    const currentRightXSquaredTerms = rightTiles.reduce( createAccumulatorFunction( 2 ), 0 );
    const currentRightXTerms = rightTiles.reduce( createAccumulatorFunction( 1 ), 0 );
    const currentRightOnesTerms = rightTiles.reduce( createAccumulatorFunction( 0 ), 0 );

    const currentLeftXSquaredTerms = leftTiles.reduce( createAccumulatorFunction( 2 ), 0 );
    const currentLeftXTerms = leftTiles.reduce( createAccumulatorFunction( 1 ), 0 );
    const currentLeftOnesTerms = leftTiles.reduce( createAccumulatorFunction( 0 ), 0 ); 

    // Get the difference in tiles for the right side since this program was added.
    const rightXSquaredDifference = BalanceRightXSquaredTerms - currentRightXSquaredTerms;
    const rightXDifference = BalanceRightXTerms - currentRightXTerms;
    const rightOnesDifference = BalanceRightOnesTerms - currentRightOnesTerms;

    // Get the difference in tiles for the left side since this program was added.
    const leftXSquaredDifference = BalanceLeftXSquaredTerms - currentLeftXSquaredTerms;
    const leftXDifference = BalanceLeftXTerms - currentLeftXTerms;
    const leftOnesDifference = BalanceLeftOnesTerms - currentLeftOnesTerms;

    // The equation will be in balance if the differences for all terms is the same
    // for both sides.
    const finalInBalance = rightXSquaredDifference === leftXSquaredDifference &&
                            rightXDifference === leftXDifference &&
                            rightOnesDifference === leftOnesDifference;

    phet.paperLand.console.log( finalInBalance );

    // Finally, update our model component representing the state of balance.
    setBooleanIsInBalance( finalInBalance );

    // BalanceSoundTrigger is just a model component that triggers sound. Add a value to play the sound.
    setBalanceSoundTrigger( BalanceSoundTrigger + 1 );
}

customCodeContainer.onProgramAddedCode:
// When this program is added, set the model components that represent the number
// of terms on each side. This means that whatever tilese are out when this program
// is added, that will be considered the baseline for balance. Note that this program
// must be added AFTER the Equation Bounds program because it requires the tileData
// array created in custom code in that program.

const tileDataProperty = phet.paperLand.getModelComponent( 'tileData' );
if ( tileDataProperty ) {
    
    // find all of the entries of tile data that are on the left side
    const tileData = tileDataProperty.value;

    // find all of the entries of tile data that are on the left side
    const rightTiles = tileData.filter( data => !data.onLeftSide );
    const leftTiles = tileData.filter( data => data.onLeftSide );

    const BalanceLeftXSquaredTerms = phet.paperLand.getModelComponent( 'BalanceLeftXSquaredTerms' );
    const BalanceLeftXTerms = phet.paperLand.getModelComponent( 'BalanceLeftXTerms' );
    const BalanceLeftOnesTerms = phet.paperLand.getModelComponent( 'BalanceLeftOnesTerms' );

    const BalanceRightXSquaredTerms = phet.paperLand.getModelComponent( 'BalanceRightXSquaredTerms' );
    const BalanceRightXTerms = phet.paperLand.getModelComponent( 'BalanceRightXTerms' );
    const BalanceRightOnesTerms = phet.paperLand.getModelComponent( 'BalanceRightOnesTerms' );

   // This is a function that we use to count the "number" of terms for a given term. We go 
   // through the tiles and increment by the number of terms on that tile if positive, and 
   // decrement by the number of terms if negative.
   const createAccumulatorFunction = term => {
      return ( accumulator, data ) => {
        if ( data.term === term ) {

          // This gets the count of the active markers
          const numberOfTerms = data.count;
          return data.negative.value ? accumulator - numberOfTerms : accumulator + numberOfTerms;
        }
        else {
          return accumulator;
        }
      }
    }

    BalanceRightXSquaredTerms.value = rightTiles.reduce( createAccumulatorFunction( 'xSquared' ), 0 );
    BalanceRightXTerms.value = rightTiles.reduce( createAccumulatorFunction( 'x' ), 0 );
    BalanceRightOnesTerms.value = rightTiles.reduce( createAccumulatorFunction( 'ones' ), 0 );

    BalanceLeftXSquaredTerms.value = leftTiles.reduce( createAccumulatorFunction( 'xSquared' ), 0 );
    BalanceLeftXTerms.value = leftTiles.reduce( createAccumulatorFunction( 'x' ), 0 );
    BalanceLeftOnesTerms.value = leftTiles.reduce( createAccumulatorFunction( 'ones' ), 0 );  
}


// A function to get a string that will print the current terms in the equation.
scratchpad.getTermStringForSide = ( tileData ) => {
    const xSquaredTiles = tileData.filter( data => data.term === 2 );   //xSquared
    const xTiles = tileData.filter( data => data.term === 1 );         // x
    const onesTiles = tileData.filter( data => data.term === 0 );      // one

    let termsString = '';

    const handleTile = ( tile, term ) => {
        if ( tile.negative && tile.count !== 0 ) {

            // always describe if it is a negative operation
            termsString += '-';
        }
        else if ( termsString.length > 0 && tile.count !== 0 ) {

            // if there are terms before this one, include the operation
            termsString += '+';
        }
        if ( tile.count > 0 ) {

            // if there are more than one in the count, include the number
            termsString += ` ${tile.count}`;
        }
        termsString += `${term}`; 
    }

    xSquaredTiles.forEach( tile => handleTile( tile, 'x^2' ) );
    xTiles.forEach( tile => handleTile( tile, 'x' ) );
    onesTiles.forEach( tile => handleTile( tile, '' ) );

    if ( termsString ) {
        return termsString;
    }
    else {
        return 'EMPTY';
    }
};

