    const index = tileData.indexOf( scratchpad.thisTileData );
    if ( index > -1 ) {
        tileData.splice( index, 1 );
        delete scratchpad.tileData;        
    }
}

// remove the model observer
phet.paperLand.removeModelObserver( 'tileData', scratchpad.observerId );
delete scratchpad.observerId;

