customCodeContainer.onProgramAddedCode:
// Setting program data to the program for some sound design.
phet.paperLand.setProgramData( paperNumber, 'term', 'xSquared' );

// Using addModelObserver lets us add the tiles/bounds programs in any order
scratchpad.observerId = phet.paperLand.addModelObserver(
  'tileData', // the bounds program will create this array when it is added
  tileData => {

    // as soon as the array is available, add this tile to it
    scratchpad.thisTileData = {
      onLeftSideProperty: phet.paperLand.getModelComponent( 'BooleanOnLeftSideForXSquared_C' ),
      negativeProperty: phet.paperLand.getModelComponent( 'BooleanNegativeForXSquared_C' ),
      countProperty: phet.paperLand.getModelComponent( 'NumberXSquaredCoefficient_C' ),
      term: 'xSquared'
    }
    tileData.push( scratchpad.thisTileData );
  },
  tileData => {

    // Remove this tile from the array when the array is being removed
    const index = tileData.indexOf( scratchpad.thisTileData );
    if ( index > -1 ) {
        tileData.splice( index, 1 );
        delete scratchpad.tileData;
    }
  }
);

customCodeContainer.onProgramRemovedCode:
// Removes the term data from the model
phet.paperLand.removeProgramData( paperNumber, 'term' );

const tileData = phet.paperLand.getModelComponent( 'tileData' );
if ( tileData ) {

    // Remove the tile from the array
    const index = tileData.indexOf( scratchpad.thisTileData );
    if ( index > -1 ) {
        tileData.splice( index, 1 );
        delete scratchpad.tileData;        
    }
}

// remove the model observer
phet.paperLand.removeModelObserver( 'tileData', scratchpad.observerId );
delete scratchpad.observerId;

