// Get a reference to the model component and only update it if it exists.
const goodConnectionComponent = phet.paperLand.getModelComponent( 'BooleanGoodConnectionForX_B' );
const badConnectionComponent = phet.paperLand.getModelComponent( 'BooleanBadConnectionForX_B' );


if ( goodConnectionComponent && badConnectionComponent ) {

    // If this is the left or right whisker...
    if ( direction === 'left' || direction === 'right' ) {

        // These are the terms that this program is allowed to connect to.
        const goodConnectionTerms = [ 'xSquared' ];

        // These are the terms that this program is NOT allowed to connect to.
        const badConnectionTerms = [ 'ones' ];

        // This connection is "good" if the other program has a term in the good list
        goodConnectionComponent.value = goodConnectionTerms.includes(

            // get the term from the other program
            phet.paperLand.getProgramData( otherPaperNumber, 'term' )
        );
        // This connection is "bad" if the other program has a term in the bad list
        badConnectionComponent.value = badConnectionTerms.includes(

            // get the term from the other program
            phet.paperLand.getProgramData( otherPaperNumber, 'term' )
        );
